import { get, system } from '@styled-system/core';
import { COLORS, SIZES } from "../constants";

var transform = function transform(input, scale) {
  return get(scale && scale.icon, input, input);
};

export var styles = system({
  color: {
    scale: 'colors',
    defaultScale: COLORS,
    properties: ['color']
  },
  size: {
    scale: 'sizes',
    defaultScale: {
      icon: SIZES
    },
    properties: ['width', 'height'],
    transform: transform
  }
});
export var hoverStyles = system({
  hoverColor: {
    scale: 'colors',
    defaultScale: COLORS,
    properties: ['color']
  }
});