import { useMemo } from 'react';
import { useFormatDayOfWeek } from "./useFormatDayOfWeek";

var normalizeDayOfWeek = function normalizeDayOfWeek(day, firstDayOfWeek) {
  return day < firstDayOfWeek ? 6 - firstDayOfWeek + 1 + day : day - firstDayOfWeek;
};

export var useFormatDaysOfWeek = function useFormatDaysOfWeek(locale, firstDayOfWeek) {
  var format = useFormatDayOfWeek(locale);
  return useMemo(function () {
    return function (value) {
      var daysOfWeek = [].concat(value);
      daysOfWeek.sort(function (day1, day2) {
        return normalizeDayOfWeek(day1, firstDayOfWeek) - normalizeDayOfWeek(day2, firstDayOfWeek);
      });
      return daysOfWeek.map(function (dayOfWeek) {
        return format(dayOfWeek);
      }).join(', ');
    };
  }, [firstDayOfWeek, format]);
};