import { useMemo } from 'react';
import { DateUtils } from 'react-day-picker';
import { MONTHS_IN_YEAR, MONTH_PICKER_MONTHS_AT_ROW } from "../constants";
import { useFormatMonth, useFormatMonthShort } from "./useFormatMonth";
export var useMonthsLayout = function useMonthsLayout(year, locale) {
  var formatMonth = useFormatMonth(locale);
  var formatMonthShort = useFormatMonthShort(locale);
  return useMemo(function () {
    var date = new Date(year);
    var result = [[]];
    var currentRow = result[0];

    for (var i = 0; i < MONTHS_IN_YEAR; i++) {
      var month = DateUtils.addMonths(date, i);

      if (currentRow.length === MONTH_PICKER_MONTHS_AT_ROW) {
        currentRow = [];
        result.push(currentRow);
      }

      currentRow.push({
        monthValue: month,
        label: formatMonth(month),
        title: formatMonthShort(month)
      });
    }

    return result;
  }, [year, formatMonth, formatMonthShort]);
};