import { useMemo } from 'react';
import { DateUtils } from 'react-day-picker';
import { MONTHS_IN_YEAR, YEAR_PICKER_YEARS_AT_ROW, YEAR_PICKER_YEARS_QUANTITY } from "../constants";
export var useYearsLayout = function useYearsLayout(initialYear) {
  return useMemo(function () {
    var date = new Date(initialYear);
    var result = [[]];
    var currentRow = result[0];

    for (var i = 0; i < YEAR_PICKER_YEARS_QUANTITY; i++) {
      var year = DateUtils.addMonths(date, i * MONTHS_IN_YEAR);

      if (currentRow.length === YEAR_PICKER_YEARS_AT_ROW) {
        currentRow = [];
        result.push(currentRow);
      }

      currentRow.push({
        yearValue: year,
        title: String(year.getFullYear())
      });
    }

    return result;
  }, [initialYear]);
};