import styled from 'styled-components';
import { Space } from "../../constants";
import { Badge, BadgeSize, BadgeVariant } from "../Badge";

function getBadgeAttrs(props) {
  if (props.margin) {
    return null;
  }

  switch (props.variant) {
    case BadgeVariant.DOT:
      {
        return {
          margin: Space.N_2
        };
      }

    default:
      return {
        margin: Space.N_8
      };
  }
}

export var IconButtonBadge = /*#__PURE__*/styled(Badge).attrs(getBadgeAttrs).withConfig({
  displayName: "IconButtonBadge",
  componentId: "f4gzaw-0"
})(["z-index:1;place-self:start end;"]);
IconButtonBadge.defaultProps = {
  size: BadgeSize.SIZE_16
};