import { createContext, useContext } from 'react';

/** @private */
export var ProgressStepsContext = /*#__PURE__*/createContext(null);
/** @private */

export function useProgressStepsContext() {
  var value = useContext(ProgressStepsContext);

  if (value === null) {
    throw new Error('You can access `ProgressStepsContext` only in components inside `ProgressSteps` component');
  }

  return value;
}
