import { css } from 'styled-components';
import { Breakpoint } from "../../constants";
import { mq } from "../../styles";
import { TabBarBehaviour, TabBarVariant } from "./constants";
export function getBehaviourStyles(behaviour, variant) {
  switch (behaviour) {
    case TabBarBehaviour.SCROLL:
      return css(["overflow:auto;"]);

    case TabBarBehaviour.WRAP:
      return css(["flex-wrap:wrap;"]);

    case TabBarBehaviour.AUTO:
      switch (variant) {
        case TabBarVariant.NAVIGATION:
          return css(["@media ", "{flex-wrap:wrap;}@media not ", "{overflow:auto;}"], mq(Breakpoint.MD), mq(Breakpoint.MD));

        case TabBarVariant.SEGMENTED:
          return css(["flex-wrap:nowrap;"]);

        case TabBarVariant.DEFAULT:
          return css(["overflow:auto;"]);
      }

  }
}