import { useRef } from 'react';
import { useDocument } from "./useDocument";
import { getGlobalScrollingElement, getScrollEventTarget } from "../utils";
import { useUniversalLayoutEffect } from "./useUniversalLayoutEffect";
export function useGlobalScrollRef() {
  var doc = useDocument();
  var ref = useRef(null);
  useUniversalLayoutEffect(function () {
    if (doc) {
      ref.current = getScrollEventTarget(getGlobalScrollingElement(doc));
      return function () {
        ref.current = null;
      };
    }
  }, [doc, ref]);
  return ref;
}