import { useCallback, useEffect } from 'react';
import { useWindow } from "./useWindow";

var useKey = function useKey(eventName, key, handler) {
  var win = useWindow();
  var eventHandler = useCallback(function (event) {
    if (event.key === key) {
      return handler(event);
    }
  }, [key, handler]);
  useEffect(function () {
    if (win === null) return;
    win.addEventListener(eventName, eventHandler);
    return function () {
      return win.removeEventListener(eventName, eventHandler);
    };
  }, [win, eventName, eventHandler]);
};

export var useKeyUp = function useKeyUp(key, handler) {
  return useKey('keyup', key, handler);
};
export var useKeyDown = function useKeyDown(key, handler) {
  return useKey('keydown', key, handler);
};