import { useEffect } from 'react';
import { getGlobalScrollingElement } from "../utils";
import { useDocument } from "./useDocument";

function getLevel(element) {
  return Number(element.dataset.useScrollLockLevel || 0);
}

export function useScrollLock(condition) {
  if (condition === void 0) {
    condition = true;
  }

  var doc = useDocument();
  useEffect(function () {
    if (condition && doc !== null) {
      var element = getGlobalScrollingElement(doc);
      var initial = getLevel(element);
      element.dataset.useScrollLockLevel = "" + (initial + 1);
      if (initial === 0) element.style.overflow = 'hidden';
      return function () {
        var current = getLevel(element) - 1;
        element.dataset.useScrollLockLevel = "" + current;
        if (current === 0) element.style.overflow = '';
      };
    }
  }, [doc, condition]);
}