import { system } from '@styled-system/core';
import { BASE_SPACE } from "../constants";

function transformAlign(input) {
  switch (input) {
    case 'start':
      return 'flex-start';

    case 'end':
      return 'flex-end';

    default:
      return input;
  }
}
/** @private */


export var stack = system({
  gap: {
    property: 'gap',
    scale: 'space',
    defaultScale: BASE_SPACE
  },
  align: {
    property: 'alignItems',
    transform: transformAlign
  }
});